import React, { FunctionComponent, useState } from 'react';
import Project from '../models/project';
import './projectCard.scss';
import formatDate from '../helpers/formatDate';
import formatType from '../helpers/formatType';

type Props = {
    project : Project,
    borderColor?:string,
};

const ProjectCard: FunctionComponent<Props> = ({project, borderColor="#01859c"}) => {
    
    const [color, setColor] = useState<string>();

    const showBorder = () => {
        setColor(borderColor);
    }

    const hideBorder = () => {
        setColor("rgb(0, 98, 211)");
    }
    
    return (
        <div>
            <div className="col s4 m3 l2 card-col" onMouseEnter={showBorder} onMouseLeave={hideBorder}>
                <a href={project.link} target="_blank">
                    <div className="" style={{ borderColor: color }}>
                        <div className="card-image">
                            <img src={project.picture} alt={project.name}/>
                        </div>
                        <div className="card-stacked">
                            <div className="card-content">
                                <p>{project.name}</p>
                                <p>{project.techno}</p>
                                <p><small>{formatDate(project.created)}</small></p>
                                {project.techno.map(tech =>(
                                    <span key={tech} className={formatType(tech)}>{tech}</span>
                                ))}
                            </div>
                        </div>
                    </div>
                </a>
            </div>
        </div>
    );
}

export default ProjectCard;
