import React, { FunctionComponent, useState } from 'react';
import Project from '../models/project';
import './projectCard.css';

type Props = {
    project : Project,
    borderColor?:string,
};

const ProjectCard: FunctionComponent<Props> = ({project, borderColor="#01859c"}) => {
    
    const [color, setColor] = useState<string>();

    const showBorder = () => {
        setColor(borderColor);
    }

    const hideBorder = () => {
        setColor("rgb(0, 98, 211)");
    }

    const formatDate = (date : Date) : string => {
        return `${date.getDate()}-${date.getMonth()+1}-${date.getFullYear()}`;
    }
    
    const formatType = (type:string) : string => {
        let color : string ;
        switch (type) {
            case 'Html':
                color = 'red lighten-1'
                break;
            case 'CSS':
                color = 'blue lighten-1'
                break;
            case 'Sass':
                color = 'green lighten-1'
                break;
            case 'Laravel':
                color = 'gry lighten-1'
                break;
        }
    }
    return (
        <div>
            <div className="col s6 m4" onMouseEnter={showBorder} onMouseLeave={hideBorder}>
                <a href={project.link}>
                    <div className="card vertical" style={{ borderColor: color }}>
                        <div className="card-image">
                            <img src={project.picture} alt={project.name}/>
                        </div>
                        <div className="card-stacked">
                            <div className="card-content">
                                <p>{project.name}</p>
                                <p>{project.techno}</p>
                                <p><small>{formatDate(project.created)}</small></p>
                                {project.techno.map(tech =>(
                                    <span key={tech}>{tech}</span>
                                ))}
                            </div>
                        </div>
                    </div>
                </a>
            </div>
        </div>
    );
}

export default ProjectCard;
